import styles from '../styles'

export default ({ name, image, tags }) => (
  <article>
    {image && (
      <div className='image-container'>
        <img src={image} alt={`${name} thumbnail`} />
      </div>
    )}
    <h1>{name}</h1>
    {tags && <p>{tags.map((tag) => `#${tag}`).join(' ')}</p>}

    <style jsx>{`
      article {
        ${styles.surface}
        padding: 22px;
        height: fit-content;
      }

      img {
        width: 100%;
        display: block;
        border-radius: 4px;
      }

      h1 {
        font-size: 1rem;
      }

      h1, p {
        margin: 0;
      }

      img {
        margin-bottom: 8px;
      }

      p {
        margin-top: 8px;
        ${styles.text.muted}
      }
    `}</style>
  </article>
)
