import styles from '../styles'

export default ({ name, image, tags }) => (
  <article>
    <a href='#'>
      {image && <img src={image} alt={`${name} thumbnail`} />}
      <h1>{name}</h1>
      {tags && <p>{tags.map((tag) => `#${tag}`).join(' ')}</p>}
    </a>

    <style jsx>{`
      article {
        height: fit-content;
      }

      a {
        text-decoration: none;
        ${styles.surface}
        padding: 22px;
        display: block;
        outline: none;
      }

      img {
        width: 100%;
        display: block;
        border-radius: 4px;
        margin-bottom: 8px;
      }

      h1 {
        font-size: 1rem;
      }

      h1, p {
        margin: 0;
      }

      p {
        margin-top: 8px;
        ${styles.text.muted}
      }
    `}</style>
  </article>
)
