import fetch from 'isomorphic-unfetch'

export default {
  name: 'YouTube',
  search: async (query) => {
    const res = await fetch(`https://www.googleapis.com/youtube/v3/search?part=${encodeURIComponent('snippet,topicDetails')}&q=${encodeURIComponent(query)}&key=${encodeURIComponent(process.env.YOUTUBE_API_KEY)}`, {
      headers: {  'Accept': 'application/json' }
    })
    const json = await res.json()
    console.log(json.error.errors)
    console.log(json.topicDetails)
    return json.items.map((item) => ({
      name: item.snippet.title,
      description: item.snippet.description,
      tags: query.split(' '),
      uploadDate: Date.parse(item.snippet.publishedAt),
      url: `https://youtube.com/watch?v=${item.id.videoId}`,
      image: item.snippet.thumbnails.default.url
    }))
  }
}
