import 'dotenv'
import fetch from 'isomorphic-unfetch'

export default {
  name: 'YouTube',
  search: async (query) => {
    const res = await fetch(`https://www.googleapis.com/youtube/v3/search?part=snippet&q=${encodeURIComponent(query)}&key=${encodeURIComponent(process.env.YOUTUBE_API_KEY)}`, {
      headers: {  'Accept': 'application/json' }
    })
    const json = await res.json()
    return json.items.map((item) => ({
      name: item.snippet.title,
      description:item.snippet.title,
      tags: query.split(' '),
      uploadDate: new Date(),
      url: 'https://example.com/',
      image: 'https://prod-ripcut-delivery.disney-plus.net/v1/variant/disney/1C14D27F582C0ADB8BC1080627E6D862D4726FBB770B1CDF5996A34BA59CAF24/scale?width=800&aspectRatio=1.78&format=jpeg'
    }))
  }
}
